if ('serviceWorker' in navigator) {
    window.addEventListener('load', function() {
        // Blogger Redirects /sw.js to the GitHub deployment link
        navigator.serviceWorker.register('/sw.js').then(function(registration) {
            console.log('Service Worker registered successfully with scope: ', registration.scope);
        }, function(err) {
            console.error('Service Worker registration failed: ', err);
        });
    });
}
//

//
let scrollMarks = [25, 50, 75, 100];
let scrolledMarks = [];
window.addEventListener('scroll', function() {
    const docHeight = document.documentElement.scrollHeight - window.innerHeight;
    const scrollPosition = window.scrollY;
    const scrollPercentage = Math.round((scrollPosition / docHeight) * 100);
    for (const mark of scrollMarks) {
        if (scrollPercentage >= mark && !scrolledMarks.includes(mark)) {
            scrolledMarks.push(mark);
            if (typeof gtag !== 'undefined') {
                gtag('event', 'scroll_depth', {
                    'event_category': 'Engagement',
                    'event_label': mark + '%',
                    'value': parseInt(mark)
                });
            }
        }
    }
});
let startTime = new Date().getTime();
window.addEventListener('beforeunload', function() {
    const timeSpent = Math.round((new Date().getTime() - startTime) / 1000);
    if (typeof gtag !== 'undefined') {
        gtag('event', 'time_on_page', {
            'event_category': 'Engagement',
            'event_label': window.location.pathname,
            'value': timeSpent
        });
    }
});
document.addEventListener('click', function(e) {
    const link = e.target.closest('a');
    if (link && link.hostname !== window.location.hostname) {
        if (typeof gtag !== 'undefined') {
            gtag('event', 'click', {
                'event_category': 'Outbound Link',
                'event_label': link.href,
                'transport_type': 'beacon'
            });
        }
    }
});
