themeToggle.addEventListener('click', function() {
    let theme = document.documentElement.getAttribute('data-theme');
    if (theme === 'dark') {
        document.documentElement.setAttribute('data-theme', 'light');
        localStorage.setItem('theme', 'light');
        themeIcon.textContent = 'üåô';
    } else {
        document.documentElement.setAttribute('data-theme', 'dark');
        localStorage.setItem('theme', 'dark');
        themeIcon.textContent = '‚òÄÔ∏è';
    }
});
window.addEventListener('scroll', function() {
    const winScroll = document.body.scrollTop || document.documentElement.scrollTop;
    const height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
    const scrolled = (winScroll / height) * 100;
    document.getElementById('reading-progress').style.width = scrolled + '%';
});
const backToTop = document.getElementById('back-to-top');
window.addEventListener('scroll', function() {
    if (window.pageYOffset > 300) {
        backToTop.classList.add('visible');
    } else {
        backToTop.classList.remove('visible');
    }
});
backToTop.addEventListener('click', function() {
    window.scrollTo({ top: 0, behavior: 'smooth' });
});
function calculateReadingTime() {
    const postContent = document.querySelector('.post-content');
    if (postContent) {
        const text = postContent.textContent;
        const wordsPerMinute = 200;
        const words = text.trim().split(/\s+/).length;
        const readingTime = Math.ceil(words / wordsPerMinute);
        const readTimeElement = document.getElementById('read-time-value');
        if (readTimeElement) {
            readTimeElement.textContent = readingTime;
        }
    }
}
function sharePost(platform) {
    const url = encodeURIComponent(window.location.href);
    const title = encodeURIComponent(document.title);
    let shareUrl = '';
    switch(platform) {
        case 'facebook':
            shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${url}`;
            break;
        case 'twitter':
            shareUrl = `https://twitter.com/intent/tweet?url=${url}&text=${title}`;
            break;
        case 'whatsapp':
            shareUrl = `https://wa.me/?text=${title}%20${url}`;
            break;
    }
    if (shareUrl) {
        window.open(shareUrl, '_blank', 'width=600,height=400');
        if (typeof gtag !== 'undefined') {
            gtag('event', 'share', {
                'event_category': 'Social',
                'event_label': platform,
                'value': window.location.pathname
            });
        }
    }
}
let exitPopupShown = false;
const exitPopup = document.getElementById('exit-popup');
const exitPopupClose = document.getElementById('exit-popup-close');
document.addEventListener('mouseleave', function(e) {
    if (e.clientY < 10 && !exitPopupShown && !sessionStorage.getItem('exitPopupShown')) {
        exitPopup.classList.add('active');
        exitPopupShown = true;
        sessionStorage.setItem('exitPopupShown', 'true');
    }
});
exitPopupClose.addEventListener('click', function() {
    exitPopup.classList.remove('active');
});
exitPopup.addEventListener('click', function(e) {
    if (e.target === exitPopup) {
        exitPopup.classList.remove('active');
    }
});
